import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

export default function ViewExercise() {

  const[exercise, setExercise] = useState([]) 

  const {id} = useParams()

  useEffect(() => {
    loadExercise()
  }, [])

  const loadExercise = async () => {
    const result = await axios.get(`http://localhost:8080/exercises/find/${id}`)
    setExercise(result.data)
  }

  return (
    <div className="container">
        <div className="row">
            <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                <h2 className="text-center m-4">Pregled odabrane vježbe</h2>

                <div className='card'>
                    <div className='card-header'>
                        <ul className='list-group list-group-flush'>
                            <li className='list-group-item'><b>ID:{exercise.id}</b></li>
                            <li className='list-group-item'><b>Ime vježbe:{exercise.name}</b></li>
                            <li className='list-group-item'><b>Mišićna skupina:{exercise.bodyPart}</b></li>
                            <li className='list-group-item'><b>Broj serija:{exercise.numberOfSets}</b></li>
                            <li className='list-group-item'><b>Broj ponavljanja:{exercise.numberOfReps}</b></li>
                            <li className='list-group-item'><b>Radna kilaža:{exercise.weight}</b></li>
                        </ul>
                    </div>
                </div>

                <Link className="btn btn-dark mt-4" to={"/"}>Početna stranica</Link>

            </div>
        </div>
    </div>
  )
}
